mapper.select:
SELECT id, session_id, user_id, title, created_at, updated_at
FROM chat_sessions
WHERE session_id = #{sessionId}

mapper.update:
UPDATE chat_sessions
SET title = #{title}, updated_at = #{updatedAt}
WHERE session_id = #{sessionId}

UPDATE chat_sessions
SET title = #{title}, updated_at = NOW()
WHERE session_id = #{sessionId}

UPDATE chat_sessions
SET updated_at = NOW()
WHERE session_id = #{sessionId}
