mapper.update:
UPDATE exam_records
start_time = #{startTime}, submit_time = #{submitTime}, duration = #{duration}, score = #{score}, status = #{status}, violation_count = #{violationCount},
WHERE id = #{id}

UPDATE exam_records
SET status = #{newStatus}, submit_time = NOW()
WHERE exam_id = #{examId} AND status = #{oldStatus}

UPDATE exam_records
SET score = #{score}
WHERE id = #{examRecordId}

mapper.select:
